import React,{useEffect,useRef,useContext} from 'react';
import AppContext from '../context';


const SearchInput = () => {
    const inputRef = useRef(null);
    const context = useContext(AppContext);
    useEffect(() => {
        context.setRecherche(inputRef.current.value);
    }, [inputRef.current.value]);
    return (
        <> <input ref={inputRef} type="text" placeholder="Chercher un film" className="h-12 rounded-tl-lg rounded-bl-lg font-poppins pl-4"/> </>
    );

}
 
export default SearchInput;
